mapper.select:
select a.material_quality material_quality,m.material_quality_code material_quality_code,a.step_num,a.product_num from (select sum(if(s.id is null,0,1)) step_num,count(distinct p.id) product_num,p.id,p.material_quality material_quality from tams_user_feed_back b left join tams_product_technics_scheme t on b.technics_code = t.id left join tams_product_technics_scheme_work_step s on b.work_step = s.id left join tams_product p on t.product_id = p.id
and s.tool_no = #{tool_id}
and b.create_date >= #{begin_date}
and b.create_date <= #{end_date}
and p.material_quality in #{item}
group by p.id) a left join tams_material m on a.material_quality = m.material_quality_name group by a.material_quality

select p.material_quality, min(b.s) min_s,max(b.s) max_s, min(b.f) min_f,max(b.f) max_f, min(b.ap) min_ap,max(b.ap) max_ap, min(b.ae) min_ae,max(b.ae) max_ae, min(b.fn) min_fn,max(b.fn) max_fn, min(b.vc) min_vc,max(b.vc) max_vc, min(b.fz) min_fz,max(b.fz) max_fz from tams_user_feed_back b left join tams_product_technics_scheme t on b.technics_code = t.id left join tams_product_technics_scheme_work_step s on b.work_step = s.id left join tams_product p on t.product_id = p.id left join tams_material m on p.material_quality = m.material_quality_name
and s.tool_no = #{tool_id}
and b.create_date >= #{begin_date}
and b.create_date <= #{end_date}
and p.material_quality in #{item}
group by p.material_quality

select date_format(b.create_date, '%Y-%m-%d %h ') time,b.vc,b.f,b.ap,
b.make_amount/s.amount tool_life,
(s.amount*e.tool_price)/b.make_amount cost
from tams_user_feed_back b
left join tams_product_technics_scheme t on b.technics_code = t.id
left join tams_product p on t.product_id = p.id
left join tams_product_technics_scheme_work_step s on b.work_step = s.id
left join tams_tool l on s.tool_no = l.id
left join tams_tool_price e on e.tool_id = l.id


		and s.tool_no = #{tool_id}


		and p.id = #{product_id}


		and t.id = #{scheme_id}


		and  b.create_date >= #{begin_date}


		and  b.create_date <= #{end_date}


		and  b.create_date in #{item}


		and  p.material_quality in #{item}


group by date_format(b.create_date, '%Y-%m-%d %h ')

select date_format(b.create_date, '%Y-%m-%d %h ') time,
b.vc,b.f,b.ap,b.make_amount/s.amount tool_life,
l.tool_name,l.tool_label,
(s.amount*e.tool_price)/b.make_amount cost ,
(b.vc*0.618)+((s.amount*e.tool_price)/b.make_amount*0.382) mark,
if(b.vc = max(b.vc),1,0) max_vc,
if((s.amount*e.tool_price)/b.make_amount= min((s.amount*e.tool_price)/b.make_amount),1,0) min_cost,
if((b.vc*0.618)+((s.amount*e.tool_price)/b.make_amount*0.382)= max((b.vc*0.618)+((s.amount*e.tool_price)/b.make_amount*0.382)),1,0) max_mark
from tams_user_feed_back b
left join tams_product_technics_scheme t on b.technics_code = t.id
left join tams_product p on t.product_id = p.id
left join tams_product_technics_scheme_work_step s on b.work_step = s.id
left join tams_tool l on s.tool_no = l.id
left join tams_tool_price e on e.tool_id = l.id


		and s.tool_no = #{tool_id}


		and p.id = #{product_id}


		and t.id = #{scheme_id}


		and  b.create_date >= #{begin_date}


		and  b.create_date <= #{end_date}


		and  b.create_date in #{item}


		and  p.material_quality in #{item}


group by date_format(b.create_date, '%Y-%m-%d %h ');

select a.tool_label,a.amount,b.amount,c.amount, concat((a.amount-b.amount)/b.amount*100,'%') yoy, concat((a.amount-c.amount)/c.amount*100,'%') qoq, a.tool_cost,b.tool_cost,c.tool_cost, concat((a.tool_cost-b.tool_cost)/b.tool_cost*100,'%') cost_yoy, concat((a.tool_cost-c.tool_cost)/c.tool_cost*100,'%') cost_qoq from (select l.tool_label,sum(s.amount) amount, sum(s.amount*e.tool_price) tool_cost from tams_user_feed_back b left join tams_product_technics_scheme t on b.TECHNICS_CODE = t.CODE left join tams_product p on t.product_id = p.id left join tams_product_technics_scheme_work_step s on b.work_step = s.id left join tams_tool l on s.tool_no = l.id left join tams_tool_price e on e.tool_id = l.id
and l.tool_label = #{tool_label}
and p.id = #{product_id}
and t.id = #{scheme_id}
and year(b.create_date) = year(#{date})
and year(b.create_date) = year(#{date}) and quarter(b.create_date) = quarter(#{date})
and year(b.create_date) = year(#{date}) and month(b.create_date) = month(#{date})
and year(b.create_date) = year(#{date}) and week(b.create_date) = week(#{date})
and year(b.create_date) = year(#{date}) and month(b.create_date) = month(#{date}) and day(b.create_date) = day(#{date})
group by l.tool_label)a LEFT JOIN (select l.tool_label,sum(s.amount) amount, sum(s.amount*e.tool_price) tool_cost from tams_user_feed_back b left join tams_product_technics_scheme t on b.technics_code = t.code left join tams_product p on t.product_id = p.id left join tams_product_technics_scheme_work_step s on b.work_step = s.id left join tams_tool l on s.tool_no = l.id left join tams_tool_price e on e.tool_id = l.id
and l.tool_label = #{tool_label}
and p.id = #{product_id}
and t.id = #{scheme_id}
and year(b.create_date) = year(date_sub(#{date},interval 1 year))
and year(b.create_date) = year(date_sub(#{date},interval 1 quarter)) and quarter(b.create_date) = quarter(date_sub(#{date},interval 1 quarter))
and year(b.create_date) = year(date_sub(#{date},interval 1 month)) and month(b.create_date) = month(date_sub(#{date},interval 1 month))
and year(b.create_date) = year(date_sub(#{date},interval 1 week)) and week(b.create_date) = week(date_sub(#{date},interval 1 week))
and year(b.create_date) = year(date_sub(#{date},interval 1 day)) and month(b.create_date) = month(date_sub(#{date},interval 1 day)) and day(b.create_date) = day(date_sub(#{date},interval 1 day))
group by l.tool_label)b on a.tool_label = b.tool_label left join (select l.tool_label,sum(s.amount) amount, sum(s.amount*e.tool_price) tool_cost from tams_user_feed_back b left join tams_product_technics_scheme t on b.technics_code = t.code left join tams_product p on t.product_id = p.id left join tams_product_technics_scheme_work_step s on b.work_step = s.id left join tams_tool l on s.tool_no = l.id left join tams_tool_price e on e.tool_id = l.id
and l.tool_label = #{tool_label}
and p.id = #{product_id}
and t.id = #{scheme_id}
and year(b.create_date) = year(date_sub(#{date},interval 1 year))
and year(b.create_date) = year(date_sub(#{date},interval 1 year)) and quarter(b.create_date) = quarter(date_sub(#{date},interval 1 year))
and year(b.create_date) = year(date_sub(#{date},interval 1 year)) and month(b.create_date) = month(date_sub(#{date},interval 1 year))
and year(b.create_date) = year(date_sub(#{date},interval 1 year)) and week(b.create_date) = week(date_sub(#{date},interval 1 year))
and year(b.create_date) = year(date_sub(#{date},interval 1 month)) and month(b.create_date) = month(date_sub(#{date},interval 1 month)) and day(b.create_date) = day(date_sub(#{date},interval 1 month))
group by l.tool_label)c on a.tool_label = c.tool_label order by a.amount desc

select l.tool_label,p.product_label,t.scheme_code,sum(s.amount) amount,
sum(s.amount*e.tool_price) tool_cost
from tams_user_feed_back b
left join tams_product_technics_scheme t on b.technics_code = t.code
left join tams_product p on t.product_id = p.id
left join tams_product_technics_scheme_work_step s on b.work_step = s.id
left join tams_tool l on s.tool_no = l.id
left join tams_tool_price e on e.tool_id = l.id


		and l.tool_label = #{tool_label}


		and p.id = #{product_id}


		and t.id = #{scheme_id}


		and  b.create_date >= #{begin_date}


		and  b.create_date <= #{end_date}

        select sum(b.qualified_amount) qualified_amount,sum(b.waste_amount) waste_amount,sum(b.make_amount) make_amount,
          avg(b.qualified_amount) avg_qualified_amount,avg(b.make_amount) avg_make_amount,
		(sum(b.waste_amount)/(sum(b.qualified_amount)+sum(b.waste_amount))) as waste_ratio
        from tams_user_feed_back b


                and  b.create_date >= #{begin_date}


                and  b.create_date <= #{end_date}

select t.id,sum(b.qualified_amount) qualified_amount, sum(b.make_amount) make_amount, sum(b.waste_amount) waste_amount, make_amount/qualified_amount ratio, sum(s.workstep_date) use_time, timestampdiff(hour,b.start_time, b.end_time) standard_time, count(distinct l.catalog_id) catalog_num, sum(s.amount) amount, sum(s.amount*e.tool_price) money, count(distinct o.id) scheme_num, count(distinct o.machine_situation) machine_num from tams_user_feed_back b left join tams_product_technics_scheme t on b.technics_code = t.id left join tams_product p on t.product_id = p.id left join tams_product_technics_scheme_work_step s on b.work_step = s.id left join tams_product_technics_scheme_process o on s.scheme_id = o.id left join tams_tool l on s.tool_no = l.video_id left join tams_tool_price e on e.tool_id = l.id
and p.id = #{product_id}
and b.create_date >= #{begin_date}
and b.create_date <= #{end_date}
and t.id in #{item}
group by t.id
